boolean validateThumbprint(def token, def certList)
{
  if(token != null && token.startsWith("Bearer "))
  {
    def accessToken = token.replace("Bearer ", "");
    def cnfValue = getAccessTokenClaim(accessToken, "cnf.x5t#S256");

    if(cnfValue != null && !(certList && !certList.isEmpty()))
    	return false;

	def expectedThumbprint = getThumbprint(certList);

    if(cnfValue == null)
      return true;
    else if(cnfValue != null && expectedThumbprint != null && cnfValue.equals(expectedThumbprint))
      return true;
    else
      return false;
  }

  return true;
}

String getThumbprint(def certList)
        throws NoSuchAlgorithmException, CertificateEncodingException {

    if(certList == null || certList.isEmpty())
          return null;

    def cert = certList.get(0);

    MessageDigest md = MessageDigest.getInstance("SHA-256");
    byte[] der = cert.getEncoded();
    md.update(der);
    byte[] digest = md.digest();
    String digestHex = DatatypeConverter.printHexBinary(digest);
    return digestHex.toLowerCase();

}

String getAccessTokenClaim(String accessToken, String claim)
{
    def accessTokenSplit = accessToken.split("\\.");
    def accessTokenBodyEncoded = accessTokenSplit[1];
    def accessTokenBody = new String(Base64.getDecoder().decode(accessTokenBodyEncoded));

    return getClaimValue(accessTokenBody, claim);
}

String getClaimValue(String responseStr, String jsonPath)
{
    ObjectMapper mapper = new ObjectMapper();       
    JsonNode resultObject = mapper.readTree(responseStr);

    String [] splitJsonPath = jsonPath.split("\\.");

    for(String path : splitJsonPath)
    {
        resultObject = resultObject.findPath(path);
    }

    return resultObject.textValue();

}

def DEFAULT_THRESHOLD = 1000;
def DEFAULT_MAX = 10000;

// - This script determines if the request should be allowed, rejected, or if it requires MFA.
// - This rule can set either one of 2 headers - X-REQUIREMFA and X-DENY. No header set means the request is allowed.
// - Amounts submitted that are greater than MAX will be denied.
// - Amounts submitted that are greater than MFA threshold, but under the max, will require MFA
// - Amounts submitted that are under the MFA threshold are allowed with no MFA requirement.
// - A downstream HTTP Header and Authentication Requirements rules are required to enforce the deny/mfa. 

if(exc?.response == null)
{
  def request = exc?.request;
  def header = request?.header;

  // cleansing headers before applying
  header?.removeFields("X-DENY");
  header?.removeFields("X-REQUIREMFA");

  def identity = exc?.identity;
  def identityAttributes = identity?.getAttributes();

  userThreshold = getThreshold(identityAttributes, DEFAULT_THRESHOLD, "payment_mfa_threshold");  
  userMax = getThreshold(identityAttributes, DEFAULT_MAX, "payment_limit");  

  int amount = getRequestedAmount(exc?.request);

  // Set deny if amount greater than max
  if(amount > userMax)
    header?.add("X-DENY", "TRUE");
  // Set require MFA if amount is greater than threshold
  else if(amount > userThreshold)
    header?.add("X-REQUIREMFA", "TRUE");
}

