/*
 * @return amount parameter sent through the request
 */
def getRequestedAmount(def request)
{
  String[] amounts = null;

  def method = request?.method?.methodName;

  // get amount from either GET or POST submissions
  if(method.equals("POST"))
    amounts = request?.getPostParams()?.get("amount");
  else if(method.equals("GET"))
    amounts = request?.getQueryStringParams()?.get("amount");

  if(amounts != null && amounts.length == 1)
  	return java.lang.Integer.parseInt(amounts[0]);

  return 0;
}

/*
 * @return user threshold
 */
def getThreshold(def identityAttributes, def defaultThreshold, def claimName)
{  
  if(identityAttributes != null)
  {
    def userThresholdObj = identityAttributes.get(claimName);
    if(userThresholdObj != null)
      return java.lang.Integer.parseInt(userThresholdObj.asText());
  }

  return defaultThreshold;
}

import java.lang.StringBuilder;

def CONST_DELIMITER = "::";

def HEADER_OBJECTCODE = "nagmemberof";
def OIDC_OBJECTCODE = "object_codes";

//cleansing header before applying
exc?.request?.header?.removeFields(HEADER_OBJECTCODE);

def identity = exc?.identity;
def identityAttributes = identity?.getAttributes();

StringBuilder headerValue = null;

def objectCodeNode = (identityAttributes)?identityAttributes.get(OIDC_OBJECTCODE):null;

if(objectCodeNode != null)
{
	def objectCodeIt = objectCodeNode.iterator();

	while(objectCodeIt.hasNext())
	{
		if(!headerValue)
		{
			headerValue = new StringBuilder();
			headerValue.append(objectCodeIt.next().asText());
		}
		else
		{
			headerValue.append(CONST_DELIMITER).append(objectCodeIt.next().asText());
		}
	}
}

exc?.request?.header?.add(HEADER_OBJECTCODE, headerValue.toString());

